import Koa = require('koa');
import Router = require('find-my-way');
export interface GenericMetadata {
    active?: boolean;
    middlewares?: (Koa.Middleware | Function)[];
    path: string;
    options: Router.RouteOptions;
    store: Record<string, any> | null;
}
export interface MethodMetadata extends GenericMetadata {
    method: Router.HTTPMethod | Router.HTTPMethod[];
}
export interface RouteMetadata extends MethodMetadata {
    active: boolean;
    middlewares: (Koa.Middleware | Function)[];
    handler: string | symbol;
    metadataMap: Map<any, any>;
}
export interface ControllerMetadata extends GenericMetadata {
}
export interface RouteOptions {
    active?: boolean;
    middlewares?: (Koa.Middleware | Function)[];
    host?: string | null;
    version?: string | null;
    store?: Record<string, any> | null;
}
