/* eslint-disable no-nested-ternary */
import { isStaging, isProd, getZone } from './env';
// https://wiki.xiaohongshu.com/pages/viewpage.action?pageId=125687108
export function getEDSHost() {
    if (process.env.EDS_HOST) {
        return process.env.EDS_HOST;
    }
    if (isProd()) {
        return getZone() === 'qcsh5'
            ? '10.11.177.173:80'
            : '10.0.223.103:80';
    }
    if (isStaging()) {
        return '10.0.84.70:80';
    }
    return '10.4.28.45:80';
}
