/// <reference types="node" />
export interface Data {
    done: boolean;
    value: {
        error: boolean;
        data: any;
    };
}
export type Callback = (err: boolean, res: any) => void;
export interface Command {
    message: string;
    callback: Callback;
}
export interface ConnectionOptions {
    ip: string;
    port: number;
    password?: string;
    connectionTimeout?: number;
    tls?: {
        key: Buffer;
        cert: Buffer;
    };
}
export type RedisOptions = Omit<ConnectionOptions, 'ip' | 'port'> & {
    name: string;
    commandTimeout?: number;
};
